import { StatusBar } from 'expo-status-bar';
import { SafeAreaView,StyleSheet, Text, View,Image,TextInput,TouchableOpacity } from 'react-native';
import { vw, vh, vmin, vmax } from 'react-native-expo-viewport-units';


export default function Home() {
  return (
    <SafeAreaView style={{flex: 1, backgroundColor: '#fff'}}>
        <View style={{background:"#fff",height:'100vh',padding:50}}>
          <View style={{alignItems:"center"}}>
            <Image
              style={{width:100,height:100,position:'relative',left:5,marginTop:"15vh"}}
              source={{
                uri: require('./assets/logo.jpg'),
              }}
            ></Image>
            <Text style={{ fontFamily: 'Ubuntu_400Regular', fontSize: 15 ,color:"tomato"}}>Welcome to</Text>
            <Text style={{ fontFamily: 'Ubuntu_400Regular', fontSize: 40 ,color:"tomato"}}>Sports Mate</Text>
          </View>

            <Text style={{justifyContent:'left',color:'grey',fontSize:12,marginTop:40}}>Email or Phone</Text>
          <View style={{alignItems:"center"}}>         
            <TextInput style={{height:50,borderRadius:5,marginTop:5,marginBottom:20,width:"100%",borderWidth:1,borderColor:'lightgrey'}}/>
          </View>

          <Text style={{justifyContent:'left',color:'grey',fontSize:12,marginTop:10}}>Password</Text>

          <View style={{alignItems:"center"}}>  
            <TextInput style={{height:50,borderRadius:5,marginTop:5,marginBottom:20,width:"100%",borderWidth:1,borderColor:'lightgrey'}} secureTextEntry={true} />
            <TouchableOpacity>
              <Text>Login</Text>
            </TouchableOpacity>
          </View>


          <View style={{alignItems:"center"}}> 
            <Image
              style={{width:150,height:150}}
              source={{
                uri: require('./assets/loading.gif'),
              }}
            ></Image>
            <Text style={{ fontFamily: 'Ubuntu_400Regular', fontSize: 12 }}>Loading...Please Wait</Text>

          </View>
        </View>  
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
